mapper.select:
SELECT , , , , , ,
FROM tb_post p LEFT OUTER JOIN tb_member m ON p.member_idx = m.member_idx LEFT OUTER JOIN tb_manager ma ON p.manager_idx = ma.manager_idx LEFT OUTER JOIN tb_inquiry i ON i.post_idx = p.post_idx LEFT OUTER JOIN tb_manager ima ON ima.manager_idx = i.answer_manager_idx LEFT OUTER JOIN tb_comment co ON p.post_idx = co.post_idx
LEFT OUTER JOIN tb_member com ON co.member_idx = com.member_idx LEFT OUTER JOIN tb_manager coma ON co.manager_idx = coma.manager_idx WHERE p.post_group = 'INQUIRY' AND p.post_idx = #{postIdx}

mapper.update:
UPDATE tb_post
SET category = #{category.name},
    title   = #{title},
    mod_date = NOW(),
    content = #{content}
WHERE post_idx = #{postIdx}

UPDATE tb_inquiry
SET secret   = #{secret},
    password = #{password}
WHERE post_idx = #{postIdx}
